import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Card from "./Card.jsx";

function App() {

  const [getText, setText] = useState();
  const [getData, setData] = useState([]);
  const [totalRepo, setRepo] = useState([]);

  // input onChange
  const handleOnChange = (event) => {
    setText(event.target.value);
  }

  // onSubmit
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(getText);
    try {
      async function getGeneralData() {
        const response = await axios.get(`https://api.github.com/search/users?q=${getText}`);
        setData(response.data.items);
        setRepo(response.data);
        console.log(getData.total_count);
        // console.log(getData);
      }
      getGeneralData();
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <>
    <div className="main-container">
    <div className="App">
        <form onSubmit={handleSubmit} >
          {/* <h1>Search GitHub Users and Their Repositories</h1> */}
          <label>Enter github username : {" "}  
            <input type="text"  onChange={handleOnChange} />
          </label>
          <button type="submit">Search</button>
        </form>
        <p style={{color:"red", marginLeft:"10px"}}>Total Count : {totalRepo.total_count}</p>
    </div>
      {/* Lower Div */}
    <div className="lower-div">

      { getData &&
        getData.map((currentElement)=>{
          console.log(currentElement);
          return (
          <Card
            key={currentElement.id}
            image={currentElement.avatar_url}
            name={currentElement.login}
            url={currentElement.html_url}
          />
        );
        })
      }
    </div>
    </div>


    </>
  );
}

export default App;
