import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Card from "./Card.jsx";

function App() {

  const [getText, setText] = useState();
  const [getData, setData] = useState([]);
  const [totalRepo, setRepo] = useState([]);

  // input onChange
  const handleOnChange = (event) => {
    setText(event.target.value);
  }

  // onSubmit
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(getText);
    try {
      async function getGeneralData() {
        const response = await axios.get(`https://api.github.com/search/users?q=${getText}`);
        console.log("i am before await");
        setData(response.data.items);
        setRepo(response.data);
        console.log("i am after await");

      }
      console.log("before error");
      getGeneralData();
    } catch (error) {

      console.log(error);
      console.log("after error");
    }
  }

  const handleCardClick = (username) =>{
    console.log("Clicked card ID:",username);
  }

  return (
    <>
    <div className="main-container">
      <div className="App">
          <form onSubmit={handleSubmit} >
            {/* <h1>Search GitHub Users and Their Repositories</h1> */}
            <h5>Search username : {" "}  
              <input type="text" width="20px" height="20px" onChange={handleOnChange} />
            </h5>
            <button type="submit">Search</button>
          </form>
          <p style={{marginLeft:"5px"}}>Total Count : <span style={{color:"red"}}>{totalRepo.total_count}</span></p>
      </div>
      {/* Lower Div */}
      <div className="lower-div">
        { getData &&
          getData.map((currentElement)=>{
            console.log(currentElement);
            return (
            <Card
              key={currentElement.id}
              image={currentElement.avatar_url}
              name={currentElement.login}
              url={currentElement.html_url}
              onClick={()=>handleCardClick(currentElement.login)}
            />
          );
          })
        }
      </div>
      <div className="last-div">
        <h2>hello</h2>
      </div>
    </div>
    </>
  );
}

export default App;
