import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
function App() {

  const [getText, setText] = useState();

  const [getData , setData] = useState([]);

  // useEffect()
  useEffect(()=>{

    try{
      async function getGeneralData(){
        const response = await axios.get(`https://api.github.com/users/barda`);
        setData(response.data);
        console.log(response.data.login);
      }
      getGeneralData();
    }catch(error){
      console.log(error);
    }

  },[]);

  // input onChange
  const handleOnChange = (event) => {
    event.preventDefault();
    setText(event.target.value);
  }

  return (
    <>
      <div className="App">
        <form style={{ marginTop: "10px" }}>
          <h1>Search GitHub Users and Their Repositories</h1>
          <h3>Enter username : {" "}
            <input type="text" width="20px" height="20px" onChange={handleOnChange} />
          </h3>
          <button type="submit" >Search</button>
        </form>
      </div>
      {/* Lower Div */}
      <div className="lower-div">
        <p>Name : {getData.data.login}</p>

      </div>
    </>
  );
}

export default App;
