import { useEffect, useState } from "react";
import "./app.css";
import axios from "axios";
import Card from "./Card.jsx";

export default function App() {
  const [name, setName] = useState();
  const [url, setUrl] = useState();
  const [totalRepo, setTotalRepo] = useState();
  const [bio, setBio] = useState();
  const [repoo, setRepo] = useState([]);
  const [getImageUrl, setImageUrl] = useState();

  useEffect(() => {
    try {
      // general data
      async function getData() {
        const res = await axios.get(
          `https://api.github.com/users/ajay-nishad25`
        );
        setName(res.data.name);
        setUrl(res.data.html_url);
        setTotalRepo(res.data.public_repos);
        setBio(res.data.bio);
        setImageUrl(res.data.avatar_url);
      }
      // repo data
      const myPromise = new Promise((resolve, reject) => {
        // Asynchronous operation
        async function getRepo() {
          const repo = await axios.get(
            `https://api.github.com/users/ajay-nishad25/repos`
          );
          const receivedRepo = repo.data;
          setRepo(receivedRepo);
          if (repo != null) {
            // promis argument resolve
            resolve(repo);
          } else {
            reject("Error: Operation failed");
          }
        }
        getRepo();
      });

      myPromise
        .then((result) => {
          console.log("Success");
        })
        .catch((error) => {
          console.error("Error:");
        });

      getData();
    } catch (error) {
      console.log(error);
      alert("There was an error fetching the data. Please try again later.");
    }
  }, []);

  return (
    <div className="App">

      <img src={getImageUrl} alt="" height="250px" width="250px" />
      <h4>
        Name : <span style={{ color: "grey" }}>{name}</span>
      </h4>
      <h4>
        Bio : <span style={{ color: "grey" }}>{bio}</span>
      </h4>
      <h4>
        URL :{" "}
        <a href={url} target={"_blank"}>
          <span style={{ color: "grey" }}>{url}</span>
        </a>
      </h4>
      <h4>
        Total Public Repo : <span style={{ color: "grey" }}>{totalRepo}</span>
      </h4>
      {repoo &&
        repoo.map((currentElement) => {
          return (
            <Card
              key={currentElement.id}
              name={currentElement.name}
              url={currentElement.html_url}
            />
          );
        })}
    </div>
  );
}
