import { useEffect, useState } from "react";
import "./App.css";
import axios from "axios";
import Card from "./Card.jsx";

function App() {

  const [getText, setText] = useState();
  const [getData, setData] = useState([]);
  const [totalRepo, setRepo] = useState([]);

  // input onChange
  const handleOnChange = (event) => {
    setText(event.target.value);
  }

  // onSubmit
  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(getText);
    try {
      async function getGeneralData() {
        const response = await axios.get(`https://api.github.com/search/users?q=${getText}`);
        setData(response.data.items);
        setRepo(response.data);
        console.log(getData.total_count);
        // console.log(getData);
      }
      getGeneralData();
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <>
      <div className="App">
        <form onSubmit={handleSubmit} style={{ marginTop: "10px" }}>
          <h1>Search GitHub Users and Their Repositories</h1>
          <h3>Enter username : {" "}
            <input type="text" width="20px" height="20px" onChange={handleOnChange} />
          </h3>
          <button type="submit">Search</button>
        </form>
      </div>
      {/* Lower Div */}
      <div className="lower-div">
        <h1 style={{color:"red"}}>Total Count : {totalRepo.total_count}</h1>
        { getData &&
          getData.map((currentElement)=>{
            console.log(currentElement);
            return (
            <Card
              key={currentElement.id}
              name={currentElement.name}
              url={currentElement.html_url}
            />
          );
          })
        }
      </div>
    </>
  );
}

export default App;
